      <h1>CensuScope</h1>
      <RouterLink v-if="authStore.isLoggedIn" to="/census">Census</RouterLink>
      <button v-if="authStore.isLoggedIn" @click="async() => {
        await logout()
      }">Logout</button>
    </nav>
